<?php    	$rzvy = session_id();	if(empty($rzvy)) session_start();ob_start();/* Include class files */include(dirname(dirname(dirname(dirname(__FILE__))))."/constants.php");include(dirname(dirname(dirname(dirname(__FILE__))))."/classes/class_frontend.php");include(dirname(dirname(dirname(dirname(__FILE__))))."/classes/class_settings.php");include("init.php");/* Create object of classes */$obj_frontend = new rzvy_frontend();$obj_frontend->conn = $conn;$obj_settings = new rzvy_settings();$obj_settings->conn = $conn;$skey = $obj_settings->get_option('rzvy_stripe_secret_key');$currency = $obj_settings->get_option('rzvy_currency');$token = $_SESSION['rzvy_stripe_token'];$obj_frontend->category_id = $_SESSION['rzvy_cart_category_id'];$obj_frontend->service_id = $_SESSION['rzvy_cart_service_id'];$category_title = $obj_frontend->readone_category_name();$service_title = $obj_frontend->readone_service_name();$description = ucwords($_SESSION['rzvy_customer_detail']['firstname'].' '.$_SESSION['rzvy_customer_detail']['lastname'])." booked appointment. ".$category_title." for ".$service_title." on ".$_SESSION['rzvy_cart_datetime']." to ".$_SESSION['rzvy_cart_end_datetime'];$email = $_SESSION['rzvy_customer_detail']['email'];$net_total = $_SESSION['rzvy_cart_nettotal'];try {	\Stripe\Stripe::setApiKey($skey);	$charge = \Stripe\Charge::create([		'amount' => round($net_total*100),		'currency' => $currency,		'description' => $description,		'source' => $token,		'receipt_email' => $email	]);		$transaction_id = $charge->id;	$_SESSION["transaction_id"] = $transaction_id;	$return_response = array ( 'status' => true, 'transaction_id'=>$transaction_id);	echo json_encode($return_response);	exit;}catch (Exception $e) {	$error = $e->getMessage();	$return_response = array ('status' => false, 'error' => $error);	echo json_encode($return_response);	exit;}